        public void Display()
        {
            
            Console.Clear();

            int startX = 40; // X-position for centering
            int startY = 10; // Y-position for centering

            for (int i = 0; i < asciiArt.Length; i++)
            {
                Console.SetCursorPosition(startX, startY + i);
                Console.Write(asciiArt[i]);
                Thread.Sleep(50); // Simulate animation effect
            }

           

            Console.SetCursorPosition(startX, startY + asciiArt.Length + 2);
            for (int i = 0; i < 50; i++)
            {
                int x = 50;
                if (x > 0)
                {
                    Console.Write("*");
                }
                Thread.Sleep(50); // Simulate animation effect
            }
            Console.SetCursorPosition(startX, startY + asciiArt.Length + 3);
            Console.Write("Press any key to continue...");
            Console.ReadKey();
        }
        public void PrintMaze()
        {
            Console.Clear();
            for (int i = 0; i < 30; i++)
            {
                for (int x = 0; x < 60; x++)
                {
                    
                    Console.Write(maze[i, x] ); // Print characters in the same line
                }
                Console.WriteLine(); // Move to the next line after printing a row
            }
